import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useLocation } from 'react-router-dom';
import './App.css';

// ç™»å…¥ API å‡½æ•¸
const loginAPI = async (email: string, password: string) => {
  const formData = new FormData();
  formData.append('username', email);
  formData.append('password', password);

  const response = await fetch('http://localhost:8008/api/v1/auth/login', {
    method: 'POST',
    body: formData,
  });

  if (!response.ok) {
    throw new Error('ç™»å…¥å¤±æ•—');
  }

  return response.json();
};

// ç™»å…¥çµ„ä»¶
const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      const result = await loginAPI(email, password);
      localStorage.setItem('token', result.access_token);
      window.location.href = '/';
    } catch (err: any) {
      setError(err.message);
    }
  };

  return (
    <div className="page">
      <h1>ç™»å…¥</h1>
      <form onSubmit={handleLogin} className="booking-form">
        <div className="form-group">
          <label>é›»å­éƒµä»¶ï¼š</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label>å¯†ç¢¼ï¼š</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        {error && <p className="error">{error}</p>}
        <button type="submit" className="btn btn-primary">ç™»å…¥</button>
      </form>
    </div>
  );
};

