import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useLocation } from 'react-router-dom';
import './App.css';

// ç°¡å–®çš„ API æœå‹™
const API_BASE = 'http://localhost:8008/api/v1';

const api = {
  login: async (email: string, password: string) => {
    const formData = new FormData();
    formData.append('username', email);
    formData.append('password', password);

    const response = await fetch(`${API_BASE}/auth/login`, {
      method: 'POST',
      body: formData,
    });

    if (!response.ok) {
      throw new Error('ç™»å…¥å¤±æ•—');
    }

    return response.json();
  },

  getBookings: async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      throw new Error('æœªç™»å…¥');
    }

    const response = await fetch(`${API_BASE}/bookings/`, {
      headers: {
        'Authorization': `Bearer ${token}`,
      },
    });

    if (!response.ok) {
      throw new Error('ç²å–é ç´„å¤±æ•—');
    }

    return response.json();
  },

  createBooking: async (bookingData: any) => {
    const token = localStorage.getItem('token');
    if (!token) {
      throw new Error('æœªç™»å…¥');
    }

    const response = await fetch(`${API_BASE}/bookings/`, {
      method: 'POST',
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(bookingData),
    });

    if (!response.ok) {
      throw new Error('å‰µå»ºé ç´„å¤±æ•—');
    }

    return response.json();
  },
};

// ç™»å…¥çµ„ä»¶
const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      const result = await api.login(email, password);
      localStorage.setItem('token', result.access_token);
      window.location.href = '/';
    } catch (err: any) {
      setError(err.message);
    }
  };

  return (
    <div className="page">
      <h1>ç™»å…¥</h1>
      <form onSubmit={handleLogin} className="booking-form">
        <div className="form-group">
          <label>é›»å­éƒµä»¶ï¼š</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label>å¯†ç¢¼ï¼š</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        {error && <p className="error">{error}</p>}
        <button type="submit" className="btn btn-primary">ç™»å…¥</button>
      </form>
    </div>
  );
};

