import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import { useBookings, useStores, useServices, useUsers, usePaymentStats, usePromotions, useNotifications } from './hooks/useAPI';
import { bookingAPI, storeAPI, serviceAPI, userAPI, paymentAPI, promotionAPI, notificationAPI } from './services/api';
import './App.css';

// 頁面組件
const Home = () => {
  const { data: bookings, loading: bookingsLoading } = useBookings();
  const { data: stores, loading: storesLoading } = useStores();
  const { data: users, loading: usersLoading } = useUsers();

  return (
    <div className="page">
      <h1>預約系統首頁</h1>
      <p>歡迎使用線上預約系統</p>

      <div className="stats-grid">
        <div className="stat-card">
          <h3>總預約數</h3>
          <p className="amount">{bookingsLoading ? '載入中...' : bookings?.length || 0}</p>
        </div>
        <div className="stat-card">
          <h3>店舖數量</h3>
          <p className="amount">{storesLoading ? '載入中...' : stores?.length || 0}</p>
        </div>
        <div className="stat-card">
          <h3>用戶數量</h3>
          <p className="amount">{usersLoading ? '載入中...' : users?.length || 0}</p>
        </div>
      </div>

      <div className="feature-grid">
        <div className="feature-card">
          <h3>線上預約</h3>
          <p>客戶可以線上預約服務時間</p>
        </div>
        <div className="feature-card">
          <h3>時間表管理</h3>
          <p>管理員工工作時間和預約時段</p>
        </div>
        <div className="feature-card">
          <h3>QR Code 確認</h3>
          <p>掃描 QR Code 快速確認預約</p>
        </div>
        <div className="feature-card">
          <h3>多員工帳號</h3>
          <p>支援多員工同時管理</p>
        </div>
      </div>
    </div>
  );
};

const Booking = () => {
  const { data: bookings, loading, error, refetch } = useBookings();
  const { data: services } = useServices();
  const { data: users } = useUsers();
  const [formData, setFormData] = useState({
    customer_name: '',
    service_id: '',
    booking_date: '',
    booking_time: '',
    employee_id: '',
    notes: ''
  });

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      await bookingAPI.create(formData);
      alert('預約成功！');
      setFormData({
        customer_name: '',
        service_id: '',
        booking_date: '',
        booking_time: '',
        employee_id: '',
        notes: ''
      });
      refetch();
    } catch (error: any) {
      alert('預約失敗：' + (error.response?.data?.detail || error.message));
    }
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement>) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  return (
    <div className="page">
      <h1>預約管理</h1>

      <div className="booking-form">
        <h2>新增預約</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>客戶姓名：</label>
            <input
              type="text"
              name="customer_name"
              value={formData.customer_name}
              onChange={handleInputChange}
              placeholder="請輸入客戶姓名"
              required
            />
          </div>
          <div className="form-group">
            <label>服務項目：</label>
            <select
              name="service_id"
              value={formData.service_id}
              onChange={handleInputChange}
              required
            >
              <option value="">請選擇服務</option>
              {services?.map((service: any) => (
                <option key={service.id} value={service.id}>
                  {service.name} - ${service.price}
                </option>
              ))}
            </select>
          </div>
          <div className="form-group">
            <label>預約日期：</label>
            <input
              type="date"
              name="booking_date"
              value={formData.booking_date}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>預約時間：</label>
            <input
              type="time"
              name="booking_time"
              value={formData.booking_time}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>員工：</label>
            <select
              name="employee_id"
              value={formData.employee_id}
              onChange={handleInputChange}
              required
            >
              <option value="">請選擇員工</option>
              {users?.filter((user: any) => user.role === 'employee').map((user: any) => (
                <option key={user.id} value={user.id}>
                  {user.name}
                </option>
              ))}
            </select>
          </div>
          <div className="form-group">
            <label>備註：</label>
            <textarea
              name="notes"
              value={formData.notes}
              onChange={handleInputChange}
              placeholder="請輸入備註"
            />
          </div>
          <button type="submit" className="btn btn-primary">確認預約</button>
        </form>
      </div>

      <div className="booking-list">
        <h2>預約列表</h2>
        {loading ? (
          <p>載入中...</p>
        ) : error ? (
          <p className="error">錯誤：{error}</p>
        ) : (
          <div className="booking-grid">
            {bookings?.map((booking: any) => (
              <div key={booking.id} className="booking-item">
                <h4>{booking.customer_name}</h4>
                <p>服務：{booking.service_name}</p>
                <p>日期：{booking.booking_date}</p>
                <p>時間：{booking.booking_time}</p>
                <p>員工：{booking.employee_name}</p>
                <p>狀態：{booking.status}</p>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

const Schedule = () => {
  const { data: bookings, loading } = useBookings();
  const { data: users } = useUsers();

  const today = new Date().toISOString().split('T')[0];
  const todayBookings = bookings?.filter((booking: any) => booking.booking_date === today) || [];

  return (
    <div className="page">
      <h1>時間表管理</h1>
      <div className="schedule-grid">
        <div className="schedule-card">
          <h3>今日預約 ({today})</h3>
          {loading ? (
            <p>載入中...</p>
          ) : todayBookings.length === 0 ? (
            <p>今日無預約</p>
          ) : (
            todayBookings.map((booking: any) => (
              <div key={booking.id} className="schedule-item">
                <span>{booking.booking_time} - {booking.employee_name} - {booking.service_name}</span>
              </div>
            ))
          )}
        </div>
        <div className="schedule-card">
          <h3>員工時間表</h3>
          {users?.filter((user: any) => user.role === 'employee').map((user: any) => (
            <div key={user.id} className="employee-schedule">
              <h4>{user.name}</h4>
              <p>工作時間：09:00-18:00</p>
              <p>休息時間：12:00-13:00</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

const Store = () => {
  const { data: stores, loading, error } = useStores();

  return (
    <div className="page">
      <h1>店舖管理</h1>
      {loading ? (
        <p>載入中...</p>
      ) : error ? (
        <p className="error">錯誤：{error}</p>
      ) : (
        <div className="store-grid">
          {stores?.map((store: any) => (
            <div key={store.id} className="store-card">
              <h3>{store.name}</h3>
              <p>地址：{store.address}</p>
              <p>電話：{store.phone}</p>
              <p>營業時間：{store.business_hours}</p>
              <button className="btn btn-primary">編輯</button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

const Customer = () => {
  const { data: users, loading, error } = useUsers();

  const customers = users?.filter((user: any) => user.role === 'customer') || [];

  return (
    <div className="page">
      <h1>客戶管理</h1>
      {loading ? (
        <p>載入中...</p>
      ) : error ? (
        <p className="error">錯誤：{error}</p>
      ) : (
        <div className="customer-list">
          {customers.map((customer: any) => (
            <div key={customer.id} className="customer-item">
              <h3>{customer.name}</h3>
              <p>電話：{customer.phone}</p>
              <p>會員等級：{customer.membership_level || '一般會員'}</p>
              <p>積分：{customer.points || 0}</p>
              <button className="btn btn-primary">查看詳情</button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

const Payment = () => {
  const { data: stats, loading, error } = usePaymentStats();

  return (
    <div className="page">
      <h1>收款管理</h1>
      {loading ? (
        <p>載入中...</p>
      ) : error ? (
        <p className="error">錯誤：{error}</p>
      ) : (
        <>
          <div className="payment-stats">
            <div className="stat-card">
              <h3>今日收入</h3>
              <p className="amount">${stats?.today_income || 0}</p>
            </div>
            <div className="stat-card">
              <h3>本月收入</h3>
              <p className="amount">${stats?.month_income || 0}</p>
            </div>
            <div className="stat-card">
              <h3>待收款</h3>
              <p className="amount">${stats?.pending_income || 0}</p>
            </div>
          </div>
          <div className="payment-methods">
            <h2>付款方式</h2>
            <div className="method-grid">
              <div className="method-item">
                <h4>現金</h4>
                <p>今日：${stats?.cash_today || 0}</p>
              </div>
              <div className="method-item">
                <h4>信用卡</h4>
                <p>今日：${stats?.card_today || 0}</p>
              </div>
              <div className="method-item">
                <h4>行動支付</h4>
                <p>今日：${stats?.mobile_today || 0}</p>
              </div>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

const Promotion = () => {
  const { data: promotions, loading, error } = usePromotions();

  return (
    <div className="page">
      <h1>促銷活動</h1>
      {loading ? (
        <p>載入中...</p>
      ) : error ? (
        <p className="error">錯誤：{error}</p>
      ) : (
        <div className="promotion-grid">
          {promotions?.map((promotion: any) => (
            <div key={promotion.id} className="promotion-card">
              <h3>{promotion.name}</h3>
              <p>{promotion.description}</p>
              <p>有效期：{promotion.valid_until}</p>
              <button className="btn btn-primary">編輯</button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

const Notification = () => {
  const { data: notifications, loading, error } = useNotifications();

  return (
    <div className="page">
      <h1>通知中心</h1>
      {loading ? (
        <p>載入中...</p>
      ) : error ? (
        <p className="error">錯誤：{error}</p>
      ) : (
        <div className="notification-list">
          {notifications?.map((notification: any) => (
            <div key={notification.id} className="notification-item">
              <h4>{notification.title}</h4>
              <p>{notification.message}</p>
              <span className="time">{notification.created_at}</span>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

function App() {
  return (
    <Router>
      <div className="App">
        <nav className="navbar">
          <div className="nav-brand">
            <h2>預約系統</h2>
          </div>
          <ul className="nav-menu">
            <li><Link to="/">首頁</Link></li>
            <li><Link to="/booking">預約管理</Link></li>
            <li><Link to="/schedule">時間表</Link></li>
            <li><Link to="/store">店舖管理</Link></li>
            <li><Link to="/customer">客戶管理</Link></li>
            <li><Link to="/payment">收款管理</Link></li>
            <li><Link to="/promotion">促銷活動</Link></li>
            <li><Link to="/notification">通知中心</Link></li>
          </ul>
        </nav>

        <main className="main-content">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/booking" element={<Booking />} />
            <Route path="/schedule" element={<Schedule />} />
            <Route path="/store" element={<Store />} />
            <Route path="/customer" element={<Customer />} />
            <Route path="/payment" element={<Payment />} />
            <Route path="/promotion" element={<Promotion />} />
            <Route path="/notification" element={<Notification />} />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
