import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import './App.css';

// 簡單的 API 服務
const API_BASE = 'http://localhost:8008/api/v1';

const api = {
  login: async (email: string, password: string) => {
    const formData = new FormData();
    formData.append('username', email);
    formData.append('password', password);

    const response = await fetch(`${API_BASE}/auth/login`, {
      method: 'POST',
      body: formData,
    });

    if (!response.ok) {
      throw new Error('登入失敗');
    }

    return response.json();
  },

  getBookings: async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      throw new Error('未登入');
    }

    const response = await fetch(`${API_BASE}/bookings/`, {
      headers: {
        'Authorization': `Bearer ${token}`,
      },
    });

    if (!response.ok) {
      throw new Error('獲取預約失敗');
    }

    return response.json();
  },

  createBooking: async (bookingData: any) => {
    const token = localStorage.getItem('token');
    if (!token) {
      throw new Error('未登入');
    }

    const response = await fetch(`${API_BASE}/bookings/`, {
      method: 'POST',
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(bookingData),
    });

    if (!response.ok) {
      throw new Error('創建預約失敗');
    }

    return response.json();
  },
};

// 登入組件
const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      const result = await api.login(email, password);
      localStorage.setItem('token', result.access_token);
      window.location.href = '/';
    } catch (err: any) {
      setError(err.message);
    }
  };

  return (
    <div className="page">
      <h1>登入</h1>
      <form onSubmit={handleLogin} className="booking-form">
        <div className="form-group">
          <label>電子郵件：</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label>密碼：</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        {error && <p className="error">{error}</p>}
        <button type="submit" className="btn btn-primary">登入</button>
      </form>
    </div>
  );
};

// 頁面組件
const Home = () => {
  const [stats, setStats] = useState({ bookings: 0, stores: 0, users: 0 });

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const bookings = await api.getBookings();
        setStats({ bookings: bookings.length, stores: 2, users: 1 });
      } catch (err) {
        console.log('無法獲取統計資料');
      }
    };

    fetchStats();
  }, []);

  return (
    <div className="page">
      <h1>預約系統首頁</h1>
      <p>歡迎使用線上預約系統</p>

      <div className="stats-grid">
        <div className="stat-card">
          <h3>總預約數</h3>
          <p className="amount">{stats.bookings}</p>
        </div>
        <div className="stat-card">
          <h3>店舖數量</h3>
          <p className="amount">{stats.stores}</p>
        </div>
        <div className="stat-card">
          <h3>用戶數量</h3>
          <p className="amount">{stats.users}</p>
        </div>
      </div>

      <div className="feature-grid">
        <div className="feature-card">
          <h3>線上預約</h3>
          <p>客戶可以線上預約服務時間</p>
        </div>
        <div className="feature-card">
          <h3>時間表管理</h3>
          <p>管理員工工作時間和預約時段</p>
        </div>
        <div className="feature-card">
          <h3>QR Code 確認</h3>
          <p>掃描 QR Code 快速確認預約</p>
        </div>
        <div className="feature-card">
          <h3>多員工帳號</h3>
          <p>支援多員工同時管理</p>
        </div>
      </div>
    </div>
  );
};

const Booking = () => {
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [formData, setFormData] = useState({
    customer_name: '',
    service_name: '',
    booking_date: '',
    booking_time: '',
    employee_name: '',
    notes: ''
  });

  useEffect(() => {
    fetchBookings();
  }, []);

  const fetchBookings = async () => {
    try {
      const data = await api.getBookings();
      setBookings(data);
      setError('');
    } catch (err: any) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      await api.createBooking(formData);
      alert('預約成功！');
      setFormData({
        customer_name: '',
        service_name: '',
        booking_date: '',
        booking_time: '',
        employee_name: '',
        notes: ''
      });
      fetchBookings();
    } catch (error: any) {
      alert('預約失敗：' + error.message);
    }
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement>) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  return (
    <div className="page">
      <h1>預約管理</h1>

      <div className="booking-form">
        <h2>新增預約</h2>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>客戶姓名：</label>
            <input
              type="text"
              name="customer_name"
              value={formData.customer_name}
              onChange={handleInputChange}
              placeholder="請輸入客戶姓名"
              required
            />
          </div>
          <div className="form-group">
            <label>服務項目：</label>
            <select
              name="service_name"
              value={formData.service_name}
              onChange={handleInputChange}
              required
            >
              <option value="">請選擇服務</option>
              <option value="美髮">美髮</option>
              <option value="美甲">美甲</option>
              <option value="按摩">按摩</option>
              <option value="美容">美容</option>
            </select>
          </div>
          <div className="form-group">
            <label>預約日期：</label>
            <input
              type="date"
              name="booking_date"
              value={formData.booking_date}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>預約時間：</label>
            <input
              type="time"
              name="booking_time"
              value={formData.booking_time}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>員工：</label>
            <select
              name="employee_name"
              value={formData.employee_name}
              onChange={handleInputChange}
              required
            >
              <option value="">請選擇員工</option>
              <option value="張美美">張美美</option>
              <option value="李小華">李小華</option>
              <option value="王小明">王小明</option>
            </select>
          </div>
          <div className="form-group">
            <label>備註：</label>
            <textarea
              name="notes"
              value={formData.notes}
              onChange={handleInputChange}
              placeholder="請輸入備註"
            />
          </div>
          <button type="submit" className="btn btn-primary">確認預約</button>
        </form>
      </div>

      <div className="booking-list">
        <h2>預約列表</h2>
        {loading ? (
          <p>載入中...</p>
        ) : error ? (
          <p className="error">錯誤：{error}</p>
        ) : bookings.length === 0 ? (
          <p>目前沒有預約</p>
        ) : (
          <div className="booking-grid">
            {bookings.map((booking: any, index: number) => (
              <div key={index} className="booking-item">
                <h4>{booking.customer_name}</h4>
                <p>服務：{booking.service_name}</p>
                <p>日期：{booking.booking_date}</p>
                <p>時間：{booking.booking_time}</p>
                <p>員工：{booking.employee_name}</p>
                <p>狀態：{booking.status || '已確認'}</p>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

