function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem('token');
    setIsLoggedIn(!!token);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    setIsLoggedIn(false);
    window.location.href = '/login';
  };

  if (!isLoggedIn) {
    return (
      <Router>
        <Routes>
          <Route path="/login" element={<Login />} />
          <Route path="*" element={<Login />} />
        </Routes>
      </Router>
    );
  }

  return (
    <Router>
      <div className="App">
        <nav className="navbar">
          <div className="nav-brand">
            <h2>預約系統</h2>
          </div>
          <ul className="nav-menu">
            <li><Link to="/">首頁</Link></li>
            <li><Link to="/booking">預約管理</Link></li>
            <li><Link to="/schedule">時間表</Link></li>
            <li><Link to="/store">店舖管理</Link></li>
            <li><Link to="/customer">客戶管理</Link></li>
            <li><Link to="/payment">收款管理</Link></li>
            <li><Link to="/promotion">促銷活動</Link></li>
            <li><Link to="/notification">通知中心</Link></li>
            <li><button onClick={handleLogout} className="btn btn-primary">登出</button></li>
          </ul>
        </nav>

        <main className="main-content">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/booking" element={<Booking />} />
            <Route path="/schedule" element={<Schedule />} />
            <Route path="/store" element={<Store />} />
            <Route path="/customer" element={<Customer />} />
            <Route path="/payment" element={<Payment />} />
            <Route path="/promotion" element={<Promotion />} />
            <Route path="/notification" element={<Notification />} />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
